<div class="btn-group">        <button data-toggle="dropdown" class="btn btn-sm blue dropdown-toggle" type="button"><i class='fa fa-cogs'></i> Actions            <span class="caret"></span>            <span class="sr-only">Toggle Dropdown</span>        </button>        <ul role="menu" class="dropdown-menu">            <li>            <?php            echo CHtml::ajaxLink("<i class='fa fa-check-square-o'></i> Pending", Yii::app()->createUrl('//admin/order/pending'), array(                'cache' => true,                'type' => 'POST',                'data' => 'js:{value : $.fn.yiiGridView.getChecked("order-grid","actionCheck[]")}',                "beforeSend" => 'js:function(){                                            var ask = confirm("Are you sure want to send into Pending?");                                            if(ask==false){                                                return false;                                            }                                        }',                'success' => 'js:function(data){                                            $.fn.yiiGridView.update("order-grid");                                            getCountTotal();                                            data = $.parseJSON (data);                                            if(data.msg=="success"){                                                //alert();                                                showAlert("success",data.totalOrders + " Orders send to pending list successfully.");                                            }else if(data.msg=="error"){                                                //alert("Error occured !!!");                                                showAlert("danger","Error occured !!!");                                            }                                        }',                'error' => 'js:function(data){                                            alert("Problem occured !!!");                                            //alert(JSON.stringify(data));                                            //alert("e"+data.msg);                                        }',                    ), array('class' => 'btn btn-xs', 'style' => 'text-align:left;color:black; font-weight:normal;', 'id' => 'pendingOrder'));            ?>        </li>            <li>                <?php                echo CHtml::ajaxLink("<i class='fa fa-check-square-o'></i> Confirmed", Yii::app()->createUrl('//admin/order/confirmed'), array(                    'cache' => true,                    'type' => 'POST',                    'data' => 'js:{value : $.fn.yiiGridView.getChecked("order-grid","actionCheck[]")}',                    "beforeSend" => 'js:function(){                                            var ask = confirm("Are you sure want to confirm this order without payment?");                                            if(ask==false){                                                return false;                                            }                                        }',                    'success' => 'js:function(data){                    //alert(data);                                            $.fn.yiiGridView.update("order-grid");                                            getCountTotal();                                            data = $.parseJSON (data);                                            if(data.msg=="success"){                                                //alert();                                                if(data.totalDue==0){                                                showAlert("success",data.totalOrders + " Orders confirmed successfully.");                                                }else if(data.totalOrders==0){                                                showAlert("danger",data.totalDue + " Orders not confirmed because of due.");                                                }else{                                                showAlert("success",data.totalOrders + " Orders confirmed successfully. And "+ data.totalDue + " Orders not confirmed because of due.");                                                }                                            }else if(data.msg=="error"){                                                //alert("Error occured !!!");                                                showAlert("danger","Error occured !!!");                                            }                                        }',                    'error' => 'js:function(data){                                            alert("Problem occured !!!");                                            //alert(JSON.stringify(data));                                            //alert("e"+data.msg);                                        }',                        ), array('class' => 'btn btn-xs', 'style' => 'text-align:left;color:black; font-weight:normal;', 'id' => 'confirmOrder'));                ?>            </li>        </ul>    </div>