<div class="btn-group">    <button data-toggle="dropdown" class="btn btn-default btn-xs dropdown-toggle" type="button"><i class='fa fa-flag'></i> Flag        <span class="caret"></span>        <span class="sr-only">Toggle Dropdown</span>    </button>    <ul role="menu" class="dropdown-menu">        <?php $flag=FlagSettings::model()->getFlag('Order BY shorting ASC');        foreach($flag as $showflag):        ?>        <li>            <?php            echo CHtml::ajaxLink('<i class="fa fa-flag" style="color:'.$showflag->color.'"></i> '.$showflag->name, Yii::app()->createUrl('//admin/order/setFlag/'.$showflag->id), array(                'cache' => true,                'type' => 'POST',                'data' => 'js:{value : $.fn.yiiGridView.getChecked("order-grid","actionCheck[]")}',                "beforeSend" => 'js:function(){                                            var ask = confirm("Are you sure want to add flag?");                                            if(ask==false){                                                return false;                                            }                                        }',                'success' => 'js:function(data){                                             $.fn.yiiGridView.update("order-grid");                                            data = $.parseJSON (data);                                            if(data.msg=="success"){                                                //alert();                                                showAlert("success","Flag added for "+data.totalFlag + " Orders successfully done.");                                                                                            }else if(data.msg=="error"){                                                //alert("Error occured !!!");                                                showAlert("danger","Error occured !!!");                                            }                                        }',                'error' => 'js:function(data){                                            alert("Problem occured !!!");                                            //alert(JSON.stringify(data));                                            //alert("e"+data.msg);                                        }',                    ), array('class' => 'btn btn-xs', 'style' => 'text-align:left;color:black; font-weight:normal;', 'id' => 'flag'.$showflag->id));            ?>        </li>        <?php endforeach;?>        <li>                    <?php                    echo CHtml::ajaxLink('<i class="fa fa-times-circle-o"></i> Remove Flag', Yii::app()->createUrl('//admin/order/setFlag/0'), array(                        'cache' => true,                        'type' => 'POST',                        'data' => 'js:{value : $.fn.yiiGridView.getChecked("order-grid","actionCheck[]")}',                        "beforeSend" => 'js:function(){                                            var ask = confirm("Are you sure want to remove flag?");                                            if(ask==false){                                                return false;                                            }                                        }',                        'success' => 'js:function(data){                                             $.fn.yiiGridView.update("order-grid");                                            data = $.parseJSON (data);                                            if(data.msg=="success"){                                                //alert();                                                showAlert("success","Flag removed for "+data.totalFlag + " Orders successfully done.");                                                                                            }else if(data.msg=="error"){                                                //alert("Error occured !!!");                                                showAlert("danger","Error occured !!!");                                            }                                        }',                        'error' => 'js:function(data){                                            alert("Problem occured !!!");                                            //alert(JSON.stringify(data));                                            //alert("e"+data.msg);                                        }',                            ), array('class' => 'btn btn-xs', 'style' => 'text-align:left;color:black; font-weight:normal;', 'id' => 'flag' .rand(1,99)));                    ?>                </li>    </ul></div>